    processPnlSchedule: function(config) {
        /**
            * Load init schedule panel
            * Load schedule display by month
            */
        var selectDay = new Date();

        //Month
        var startDate = new Date(selectDay.getFullYear(), selectDay.getMonth(), 1),
            endDate = new Date(selectDay.getFullYear(), selectDay.getMonth(), new Date(selectDay.getFullYear(), selectDay.getMonth() + 1, 0).getDate() + 1);
        var viewPreset = 'monthAndDay';


        var resourceStore = new Sch.data.ResourceStore();
        var eventStore = new Sch.data.EventStore({
            autoLoad: false,
            autoSync: true,
            batch: false,

            proxy: {
                type: 'ajax',
                extraParams: {idDate : selectDay} ,
                url: apiUrl +'schedule/LoadScheduleEvents',
                reader: {
                    type: 'json',
                    root: 'results'
                },
                writer: {
                    type: 'json',
                    root: 'results'
                },
                listeners: {
                    exception: function(proxy, response, operation, options){
                        MessageCommon.showErrorMessage(MessageCommon.MesErrorServerFail);
                    }
                }
            }
        });
        return Ext.apply(config, {
            columns: [{header: 'スタッフ名', dataIndex: 'Name'}],
            startDate: startDate,
            endDate: endDate,
            viewPreset: viewPreset,
            enableEventDragDrop: false,
            enableDragCreation: false,
            rowHeight: 35,
            eventBodyTemplate: new Ext.Template(
                '<div class="sch-event-footer">{Name}</div>'
            ),
            tooltipTpl: new Ext.XTemplate(
                '<dl class="eventTip">',
                '<dt class="icon-task">名前</dt><dd>{Name}</dd>',
                '<dt class="icon-clock">開始時間</dt><dd>{[Ext.Date.format(values.StartDate, "Y-m-d G:i")]}</dd>',
                '<dt class="icon-clock">終了時間</dt><dd>{[Ext.Date.format(values.EndDate, "Y-m-d G:i")]}</dd>',
                '</dl>',
                {
                    getField: function(obj, field) {
                        return obj[field];
                    }
                }
            ).compile(),
            resourceStore: resourceStore,
            eventStore: eventStore
        });
    }

